document.getElementById('source').addEventListener('change',function(e){var b=document.getElementById('pdf-preview'),c=e.target.files[0];if(c&&c.type==='application/pdf'){var d=new FileReader;d.onload=e=>{b.src=e.target.result;b.classList.remove('d-none')};d.readAsDataURL(c)}else{b.classList.add('d-none');c&&alert('Hanya file PDF yang diperbolehkan')}});
