document.getElementById('photo').addEventListener('change',function(e){var b=document.getElementById('image-preview'),c=e.target.files[0];if(c){var d=document.createElement('p');d.className='small text-muted mt-2';d.textContent='Preview Foto Baru:';b.parentNode.insertBefore(d,b);if(c.size>2*1024*1024){alert('Ukuran file maksimal 2MB');this.value='';return}var a=new FileReader;a.onload=e=>{b.src=e.target.result;b.classList.remove('d-none')};a.readAsDataURL(c)}else b.classList.add('d-none')});
